import 'package:finserv/presentation/add_customer.dart';import 'package:finserv/presentation/home.dart';import 'package:flutter/material.dart';class FirstScreen extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Scaffold(      body: SingleChildScrollView(child: Container(        height: MediaQuery.of(context).size.height,        width: MediaQuery.of(context).size.width,        decoration: BoxDecoration(          color: Colors.yellow,          gradient: LinearGradient(            begin: Alignment.topLeft,            end: Alignment(                0.9, 0.0), // 10% of the width, so there are ten blinds.            colors: [              Colors.greenAccent[200],              Colors.blueAccent            ], // red to yellow            tileMode: TileMode.repeated, // repeats the gradient over the canvas          ),        ),        child: Column(          mainAxisAlignment: MainAxisAlignment.center,          crossAxisAlignment: CrossAxisAlignment.center,          children: [            Text('Finserv',style: TextStyle(fontWeight: FontWeight.bold,fontSize: 50,color: Colors.white,letterSpacing: 20),),            SizedBox(height: MediaQuery.of(context).size.height*.10,),            FlatButton(                onPressed: () {Navigator.of(context).push(MaterialPageRoute(builder: (context) => NewCustomer(),));},                color: Colors.yellow,                child: Text('Add Customer')),            SizedBox(height: 30,),            FlatButton(                onPressed: () {Navigator.of(context).push(MaterialPageRoute(builder: (context) => Home(),));},                color: Colors.redAccent[200],                child: Text('Homepage'))          ],        ),      )),    );  }}